mapper.sql:
select t.*, case when days = 0 and hours = 0 and minutes = 0 then CONCAT(seconds , '秒') when days = 0 and hours = 0 then CONCAT(minutes , '分' , seconds , '秒') when days = 0 then CONCAT(hours ,'时' , minutes , '分' , seconds , '秒') ELSE CONCAT(days, '天', hours ,'时' , minutes , '分' , seconds , '秒') end as keeptimeShow from ( SELECT (floor(KeepTime / 86400)) as days, (floor(KeepTime / 3600) % 24) as hours, (floor(KeepTime / 60) % 60) as minutes, (KeepTime % 60) as seconds, a.AlarmReportID, a.VehId, a.BeginTime, a.EndTime, a.MaxSpeed, a.AlarmType, a.Longitude, a.Latitude, a.Velocity, a.Angle, a.`Local`, a.Elevation, a.Limited, a.Passed, a.Time, a.KeepTime, a.Road_Name, a.Road_Level, a.Road_Limited, a.DisposeAlarmName, a.DisposeAlarmTime, a.alarmcl, a.alarmclmsg, a.alarmhdremark, a.PushState, a.verifyName, a.verifyTime, a.AnalyzeMode, a.SysPassed, ifnull( a.`status`,0) as status, a.SynTime, a.plateNumber, a.plateNumber as plate, a.color, a.company, a.OperatType, a.EndSpeed, ROUND(ifnull(a.Distance,0)/1000,3) as Distance, a.Road_Limited2, a.IsSupplements, a.CutoffTime, a.verifyTime2, a.AlarmID, a.endLongitude, a.endLatitude, a.endRoadName, a.isRegionV, case when ifnull(isRegionV,0) = 1 then '(区域已验证)' else '' end isRegionVShow, a.AnalyzeID, a.atNocturnal, case when a.atNocturnal=1 then '夜间' else '' end atNocturnalShow, a.BaoJingType, a.ChaoSuBi, CONCAT(a.ChaoSuBi,'%') as ChaoSuBiShow, a.calctime, a.status as alarmlevel, a.alarmGuid, a.icardsign, b.chulizhuangtai, b.chulixingshi, b.chulimiaoshu, b.chulirenid, b.chuliren, b.chulishijian, b.fujian, b.beizhu, b.remark, b.shensushenhebiaoshi, b.shensushenheren, b.shensushenheshijian, b.shensushenheyijian, IFNULL(convert((a.Distance/1000)/(a.KeepTime/3600),decimal(10,2)),0) as pingjunsudu FROM baobiao_alarmsummary_cutofftime AS a
INNER JOIN ( SELECT DISTINCT xiaji.id, xiaji.parent_id, xiaji.dept_name, xiaji.is_deleted FROM blade_dept shangji, blade_dept xiaji WHERE xiaji.tree_code LIKE CONCAT(shangji.tree_code,'%') AND xiaji.is_deleted = 0 AND xiaji.extend_type='机构' and shangji.id=#{deptId} ) dept on a.company=dept.dept_name
left JOIN baobiao_alarmhandleresult AS b ON a.AlarmReportID = b.baojingid and ifnull(b.is_deleted,0) = 0 where 1=1
and cutoffTime >= CONCAT( '${beginTime}', ' 00:00:00') and cutoffTime <= CONCAT( '${endTime}', ' 23:59:59')
and passed = 100 and AnalyzeMode = 1
and AlarmType in ('无数据报警','不定位报警')
and AlarmType = #{alarmType}
and a.company like CONCAT(#{jigouName},'%')
and a.company = #{deptName}
AND ChaoSuBi >= ${chaosubi}
AND ChaoSuBi <= ${chaosubiMax}
and status = 1
) as t where 1=1

mapper.select:
select t.*, case when days = 0 and hours = 0 and minutes = 0 then CONCAT(seconds , '秒') when days = 0 and hours = 0 then CONCAT(minutes , '分' , seconds , '秒') when days = 0 then CONCAT(hours ,'时' , minutes , '分' , seconds , '秒') ELSE CONCAT(days, '天', hours ,'时' , minutes , '分' , seconds , '秒') end as keeptimeShow from ( SELECT (floor(KeepTime / 86400)) as days, (floor(KeepTime / 3600) % 24) as hours, (floor(KeepTime / 60) % 60) as minutes, (KeepTime % 60) as seconds, a.AlarmReportID, a.VehId, a.BeginTime, a.EndTime, a.MaxSpeed, a.AlarmType, a.Longitude, a.Latitude, a.Velocity, a.Angle, a.`Local`, a.Elevation, a.Limited, a.Passed, a.Time, a.KeepTime, a.Road_Name, a.Road_Level, a.Road_Limited, a.DisposeAlarmName, a.DisposeAlarmTime, a.alarmcl, a.alarmclmsg, a.alarmhdremark, a.PushState, a.verifyName, a.verifyTime, a.AnalyzeMode, a.SysPassed, ifnull( a.`status`,0) as status, a.SynTime, a.plateNumber, a.plateNumber as plate, a.color, a.company, a.OperatType, a.EndSpeed, ROUND(ifnull(a.Distance,0)/1000,3) as Distance, a.Road_Limited2, a.IsSupplements, a.CutoffTime, a.verifyTime2, a.AlarmID, a.endLongitude, a.endLatitude, a.endRoadName, a.isRegionV, case when ifnull(isRegionV,0) = 1 then '(区域已验证)' else '' end isRegionVShow, a.AnalyzeID, a.atNocturnal, case when a.atNocturnal=1 then '夜间' else '' end atNocturnalShow, a.BaoJingType, a.ChaoSuBi, CONCAT(a.ChaoSuBi,'%') as ChaoSuBiShow, a.calctime, a.status as alarmlevel, a.alarmGuid, a.icardsign, b.chulizhuangtai, b.chulixingshi, b.chulimiaoshu, b.chulirenid, b.chuliren, b.chulishijian, b.fujian, b.beizhu, b.remark, b.shensushenhebiaoshi, b.shensushenheren, b.shensushenheshijian, b.shensushenheyijian, IFNULL(convert((a.Distance/1000)/(a.KeepTime/3600),decimal(10,2)),0) as pingjunsudu FROM baobiao_alarmsummary_cutofftime AS a left JOIN baobiao_alarmhandleresult AS b ON a.AlarmReportID = b.baojingid and ifnull(b.is_deleted,0) = 0 where 1=1 and left(CutoffTime,10) = left(NOW(),10) and company = #{company}
and AlarmType in ('无数据报警','不定位报警')
and AlarmType = #{AlarmType}
and passed = 100 and AnalyzeMode = 1 ) as t where 1=1 ORDER BY t.BeginTime DESC
